import React, {useState} from 'react';

const Todo = () => {

    const [todo, setTodo] = useState()

    function Submit(e){
        e.preventDefault();
        let newTodo = {
            id: new Date().getTime(),
            content: e.target.value.trim(),
            isCompleted: false
        }
        setTodo([newTodo, ...todo])

        e.target.value = null;
    }

    return (
        <div>
            <form onSubmit={Submit}>
                <input type="text" />
                <button>click</button>
                {todo.map(t=> <Todo
                    key={t.id}
                    id={t.id}
                    todo={t.content}
                    isCompleted={t.isCompleted}
                    />)}
            </form>
            
            <ul className="list">
                <input type="checkbox"  defaultChecked={isCompleted} data-id={id} onChange={checkedElem} />
                <span>{todo}</span> <button></button>
                <li className="item"></li>
            </ul>
        </div>
    );
}

export default Todo;
